INSERT INTO `tb_job` VALUES ('3', '9', '北京六合华大基因科技有限公司校园招聘 ', '<p>招聘详情</p>\r\n\r\n<div id=\"content\" style=\"overflow: auto;\">\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">招聘岗位：技术支持</span></span><span style=\"font-size:14pt\">/</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">客服专员</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">工作地点：北京</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">岗位职责：</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">根据岗位工作规范，跟进项目</span></span><span style=\"font-size:14pt\">/</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">订单进展，及时有效地处理客户邮件和来电，确保业务顺利进行；为销售开拓市场提供技术支持；同时进行市场信息的搜集和反馈，不断提升客户满意度，达到树立华大品牌的目的。</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">任职资格：</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">1</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">、分子生物学或相关专业本科及以上学历；</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">2</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">、具有分子生物学实验室工作经验或者客服工作经验者优先考虑；</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">3</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">、具有良好的沟通表达能力及情绪控制能力，具有较强的理解力，能够及时的发现客户的问题所在并高效解决；工作态度积极乐观，能承受一定的工作强度和压力；有较强的团队协作意识。</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">待遇：</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">公司提供五险一金和规范的培训，具备明确的晋升通道；</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">可解决住宿，餐补</span></span><span style=\"font-size:14pt\">400</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">元</span></span><span style=\"font-size:14pt\">/</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">月；</span></span></p>\r\