INSERT INTO `tb_job` VALUES ('12', '12', '北京六合华大基因科技有限公司校园招聘', '<p>招聘详情</p>\r\n\r\n<div id=\"content\" style=\"overflow: auto;\">\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">招聘岗位：技术支持</span></span><span style=\"font-size:14pt\">/</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">客服专员</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">工作地点：北京</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">岗位职责：</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">根据岗位工作规范，跟进项目</span></span><span style=\"font-size:14pt\">/</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">订单进展，及时有效地处理客户邮件和来电，确保业务顺利进行；为销售开拓市场提供技术支持；同时进行市场信息的搜集和反馈，不断提升客户满意度，达到树立华大品牌的目的。</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">任职资格：</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">1</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">、分子生物学或相关专业本科及以上学历；</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">2</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">、具有分子生物学实验室工作经验或者客服工作经验者优先考虑；</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">3</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">、具有良好的沟通表达能力及情绪控制能力，具有较强的理解力，能够及时的发现客户的问题所在并高效解决；工作态度积极乐观，能承受一定的工作强度和压力；有较强的团队协作意识。</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">待遇：</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">公司提供五险一金和规范的培训，具备明确的晋升通道；</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">可解决住宿，餐补</span></span><span style=\"font-size:14pt\">400</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">元</span></span><span style=\"font-size:14pt\">/</span><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">月；</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">具体薪资面试后确定。</span></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">简历投递邮箱：</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\"><a href=\"mailto:bj.seq@service.genomics.cn\">bj.seq@service.genomics.cn</a></span></p>\r\n\r\n<p><span style=\"font-family:宋体\"><span style=\"font-size:14pt\">咨询电话：</span></span></p>\r\n\r\n<p><span style=\"font-size:14pt\">010-80400910</span></p>\r\n</div>\r\n', '2016-07-14');
INSERT INTO `tb_job` VALUES ('13', '12', '陕西海升果业发展股份有限公司校园招聘', '<p>招聘详情</p>\r\n\r\n<div id=\"content\" style=\"overflow: auto;\">\r\n<p style=\"text-align: justify;\"><strong><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">海升集团介绍</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">海升集团旗下的陕西海升果业发展股份有限公司（果汁加工事业部）是全球最大的浓缩苹果汁生产企业及出口商。</span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">2011年海升集团成立了现代农业事业部即陕西现代果业集团有限公司，通过近几年的发展，实现了苹果、柑橘、莓类、胡萝卜、梨、樱桃、猕猴桃等品类的种植布局，在全国建立了40多个果蔬种植基地，总种植面积达四万余亩。并且创建了&ldquo;清谷田园&rdquo;（EdenView）生鲜品牌。</span></span></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">集团确立打造中国果蔬领域高端品牌战略，致力于全产业链融合的发展模式，业务涉及果蔬育苗、现代化种植、果品仓储物流、生鲜销售、果蔬多元化产品精深加工、现代农业观光旅游等，已经成为一家品牌化运营的大型现代农业集团。</span></span></p>\r\n\r\n<p style=\"text-align: justify;\">&nbsp;&nbsp;<strong><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">海升乾县分公司简介</span></span></strong></p>\r\n\r\n<p><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">陕西海升乾县分公司为海升集团最早成立的分公司，于1996年建厂，总资产达</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">3</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">亿多元，占地面积</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">180</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">多亩，位于陕西省北部的乾县。公司现有正式员工</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">200</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">余人，大专以上学历占</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">65%</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">以上。公司具有先进卓越的企业文化理念，坚持以人为本的管理理念，重视人才的培养和激励。公司为员工提供优厚的薪酬、福利并提供完善的社会保障。</span></span><span style=\"font-size:12pt\"><span style=\"font-family:宋体\">作为国家级农业产业化龙头企业之一，乾县海升在全力打造果汁主业的同时，围绕陕西特色苹果产业的发展，依托陕西地区的生态环境和现代农业高新科技水平，在2013年春从荷兰、意大利等先进苗木生